// src/components/AddModal.js
import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

export default function AddModal({ type, onAdd, onClose }) {
  const [name, setName] = useState('');
  const [categories, setCategories] = useState([]);
  const [topics, setTopics] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedTopic, setSelectedTopic] = useState('');

  useEffect(() => {
    if (type === 'topic' || type === 'list') {
      fetch('http://localhost:5000/forum')
        .then(res => res.json())
        .then(data => {
          setCategories(data);
          if (type === 'list') {
            const allTopics = data.flatMap(cat =>
              cat.topics.map(topic => ({
                ...topic,
                categoryName: cat.name,
              }))
            );
            setTopics(allTopics);
          }
        });
    }
  }, [type]);

  const handleSubmit = () => {
    if (type === 'category') {
      onAdd({ name });
    } else if (type === 'topic') {
      onAdd({ name, categoryId: selectedCategory });
    } else if (type === 'list') {
      onAdd({ title: name, topicId: selectedTopic });
    }
  };

  const getTitle = () => {
    switch (type) {
      case 'category': return 'Add Category';
      case 'topic': return 'Add Topic';
      case 'list': return 'Add List';
      default: return 'Add Item';
    }
  };

  return (
    <Modal show onHide={onClose}>
      <Modal.Header closeButton>
        <Modal.Title>{getTitle()}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          {(type === 'topic') && (
            <Form.Group className="mb-3">
              <Form.Label>Select Category</Form.Label>
              <Form.Select value={selectedCategory} onChange={e => setSelectedCategory(e.target.value)}>
                <option value="">-- Select Category --</option>
                {categories.map(cat => (
                  <option key={cat._id} value={cat._id}>{cat.name}</option>
                ))}
              </Form.Select>
            </Form.Group>
          )}

          {type === 'list' && (
            <Form.Group className="mb-3">
              <Form.Label>Select Topic</Form.Label>
              <Form.Select value={selectedTopic} onChange={e => setSelectedTopic(e.target.value)}>
                <option value="">-- Select Topic --</option>
                {topics.map(topic => (
                  <option key={topic._id} value={topic._id}>
                    {topic.name} ({topic.categoryName})
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
          )}

          <Form.Group>
            <Form.Label>
              {type === 'list' ? 'List Title' : 'Name'}
            </Form.Label>
            <Form.Control
              type="text"
              placeholder={`Enter ${type} name`}
              value={name}
              onChange={e => setName(e.target.value)}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>Cancel</Button>
        <Button variant="primary" onClick={handleSubmit}>Add</Button>
      </Modal.Footer>
    </Modal>
  );
}
