// src/components/AddModal.js
import React, { useState, useEffect, useRef } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

export default function AddModal({ type, onAdd, onClose }) {
  const [formData, setFormData] = useState({
    name: '',
    categoryId: '',
    codebase: '',
    output: '',
    status: '',
  });

  const inputRef = useRef(null);

  useEffect(() => {
    if (inputRef.current) inputRef.current.focus();
  }, []);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = () => {
    if (!formData.name.trim()) {
      alert('Name is required');
      return;
    }

    const payload =
      type === 'topic'
        ? {
            name: formData.name,
            categoryId: formData.categoryId,
            codebase: formData.codebase,
            output: formData.output,
            status: formData.status,
          }
        : { name: formData.name };

    onAdd(payload);
  };

  return (
    <Modal show onHide={onClose}>
      <Modal.Header closeButton>
        <Modal.Title>Add {type}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="formName" className="mb-3">
            <Form.Label>{type} Name</Form.Label>
            <Form.Control
              ref={inputRef}
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder={`Enter ${type} name`}
              required
              autoFocus
            />
          </Form.Group>

          {type === 'topic' && (
            <>
              <Form.Group controlId="formCategoryId" className="mb-3">
                <Form.Label>Category ID</Form.Label>
                <Form.Control
                  type="text"
                  name="categoryId"
                  value={formData.categoryId}
                  onChange={handleChange}
                  placeholder="Enter Category ID"
                />
              </Form.Group>

              <Form.Group controlId="formCodebase" className="mb-3">
                <Form.Label>Codebase</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  name="codebase"
                  value={formData.codebase}
                  onChange={handleChange}
                  placeholder="Paste your code here"
                />
              </Form.Group>

              <Form.Group controlId="formOutput" className="mb-3">
                <Form.Label>Output</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={2}
                  name="output"
                  value={formData.output}
                  onChange={handleChange}
                  placeholder="Enter output of code"
                />
              </Form.Group>

              <Form.Group controlId="formStatus" className="mb-3">
                <Form.Label>Status</Form.Label>
                <Form.Control
                  type="text"
                  name="status"
                  value={formData.status}
                  onChange={handleChange}
                  placeholder="Enter status (e.g., Working/Needs Fix)"
                />
              </Form.Group>
            </>
          )}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Add
        </Button>
      </Modal.Footer>
    </Modal>
  );
}
