import React, { useState } from 'react';

export default function AddModal({ type, open, onClose, onSave, parentId }) {
  const [input, setInput] = useState('');

  const handleSubmit = () => {
    if (input.trim()) {
      onSave(input, parentId);
      setInput('');
      onClose();
    }
  };

  if (!open) return null;

  const label = {
    category: 'Category Name',
    topic: 'Topic Name',
    list: 'List Title'
  }[type];
const AddModal = ({ onAdd, onClose, type }) => {
  const [name, setName] = useState("");

  const handleSubmit = () => {
    if (name.trim()) {
      onAdd({ name });      // Calls the function passed from Header
    }
  };

  return (
    <div className="modal show d-block">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Add {type}</h5>
            <button type="button" className="btn-close" onClick={onClose}></button>
          </div>
          <div className="modal-body">
            <input
              type="text"
              className="form-control"
              placeholder={`Enter ${type} name`}
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="modal-footer">
            <button className="btn btn-secondary" onClick={onClose}>Cancel</button>
            <button className="btn btn-primary" onClick={handleSubmit}>Add</button>
          </div>
        </div>
      </div>
    </div>
  );
};
}
// This component renders a modal for adding categories, topics, or lists.
