// src/App.js
import React, { useState } from 'react';
import Header from './components/Header';
import AddModal from './components/AddModal';
import { Toast, ToastContainer } from 'react-bootstrap';

function App() {
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [toastMessage, setToastMessage] = useState('');
  const [showToast, setShowToast] = useState(false);

  const handleAdd = (type) => {
    setModalType(type);
    setShowModal(true);
  };

  const handleSubmit = async (data) => {
    const url =
      modalType === 'category'
        ? 'http://localhost:5000/category'
        : 'http://localhost:5000/topic';

    const response = await fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });

    const result = await response.json();
    console.log(`${modalType} added:`, result);

    setShowModal(false);

    // Show toast with category or topic name
    const name = modalType === 'category' ? data.name : data.name;
    setToastMessage(`${modalType.charAt(0).toUpperCase() + modalType.slice(1)} "${name}" added successfully`);
    setShowToast(true);
  };

  return (
    <>
      <Header onAdd={handleAdd} />

      {showModal && (
        <AddModal
          type={modalType}
          onAdd={handleSubmit}
          onClose={() => setShowModal(false)}
        />
      )}

      {/* Toast */}
      <ToastContainer position="bottom-center" className="p-9">
        <Toast
          onClose={() => setShowToast(false)}
          show={showToast}
          delay={5000}
          autohide
          bg="success"
        >
          <Toast.Header closeButton={false}>
            <strong className="me-auto">Forum App</strong>
          </Toast.Header>
          <Toast.Body className="text-white">{toastMessage}</Toast.Body>
        </Toast>
      </ToastContainer>
    </>
  );
}

export default App;
