// src/components/AddModal.js
import React, { useEffect, useRef, useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

export default function AddModal({ type, onAdd, onClose }) {
  const [formData, setFormData] = useState({ name: '', categoryId: '', title: '' });
  const [error, setError] = useState('');
  const inputRef = useRef(null);

  // Auto-focus the input field when modal opens
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, name: e.target.value });
    setError('');
  };

  const handleSubmit = () => {
    if (!formData.name.trim()) {
      setError('Name is required');
      return;
    }

    // Call onAdd with correct data structure
    const data = type === 'topic'
      ? { name: formData.name, categoryId: formData.categoryId }
      : type === 'list'
        ? { title: formData.name, topicId: formData.categoryId }
        : { name: formData.name };

    onAdd(data);
  };

  const getTitle = () => {
    if (type === 'category') return 'Add Category';
    if (type === 'topic') return 'Add Topic';
    if (type === 'list') return 'Add List';
    return '';
  };

  return (
    <Modal show onHide={onClose}>
      <Modal.Header closeButton>
        <Modal.Title>{getTitle()}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form.Group>
          <Form.Label>{type === 'list' ? 'Title' : 'Name'}</Form.Label>
          <Form.Control
            type="text"
            ref={inputRef}
            value={formData.name}
            onChange={handleChange}
            isInvalid={!!error}
            placeholder={`Enter ${type}`}
          />
          <Form.Control.Feedback type="invalid">{error}</Form.Control.Feedback>
        </Form.Group>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>Cancel</Button>
        <Button variant="primary" onClick={handleSubmit}>Add</Button>
      </Modal.Footer>
    </Modal>
  );
}
