// src/components/AddModal.js
import React, { useState, useEffect, useRef } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

export default function AddModal({ type, onAdd, onClose, categories = [] }) {
  const [formData, setFormData] = useState({
    name: '',
    categoryId: '',
    codebase: '',
    output: '',
    status: 'Active',
  });

  const [errors, setErrors] = useState({});
  const inputRef = useRef(null);

  useEffect(() => {
    if (inputRef.current) inputRef.current.focus();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    setErrors((prev) => ({ ...prev, [name]: '' }));
  };

  const handleSubmit = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (type === 'topic' && !formData.categoryId) newErrors.categoryId = 'Please select a category';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    const payload = type === 'topic'
      ? {
          name: formData.name,
          categoryId: formData.categoryId,
          codebase: formData.codebase,
          output: formData.output,
          status: formData.status,
        }
      : { name: formData.name };

    onAdd(payload);
  };

  return (
    <Modal show onHide={onClose}>
      <Modal.Header closeButton>
        <Modal.Title>Add {type}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="formName" className="mb-3">
            <Form.Label>{type} Name</Form.Label>
            <Form.Control
              ref={inputRef}
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              isInvalid={!!errors.name}
              placeholder={`Enter ${type} name`}
            />
            <Form.Control.Feedback type="invalid">{errors.name}</Form.Control.Feedback>
          </Form.Group>

          {type === 'topic' && (
            <>
              <Form.Group controlId="formCategoryId" className="mb-3">
                <Form.Label>Category</Form.Label>
                <Form.Select
                  name="categoryId"
                  value={formData.categoryId}
                  onChange={handleChange}
                  isInvalid={!!errors.categoryId}
                >
                  <option value="">-- Select Category --</option>
                  {categories.map((cat) => (
                    <option key={cat._id} value={cat._id}>
                      {cat.name}
                    </option>
                  ))}
                </Form.Select>
                <Form.Control.Feedback type="invalid">{errors.categoryId}</Form.Control.Feedback>
              </Form.Group>

              <Form.Group controlId="formCodebase" className="mb-3">
                <Form.Label>Codebase</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  name="codebase"
                  value={formData.codebase}
                  onChange={handleChange}
                  placeholder="Paste your code here"
                />
              </Form.Group>

              <Form.Group controlId="formOutput" className="mb-3">
                <Form.Label>Output</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={2}
                  name="output"
                  value={formData.output}
                  onChange={handleChange}
                  placeholder="Enter output"
                />
              </Form.Group>

              <Form.Group controlId="formStatus" className="mb-3">
                <Form.Label>Status</Form.Label>
                <Form.Select
                  name="status"
                  value={formData.status}
                  onChange={handleChange}
                >
                  <option value="Active">Active</option>
                  <option value="Inactive">Inactive</option>
                </Form.Select>
              </Form.Group>
            </>
          )}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>Cancel</Button>
        <Button variant="primary" onClick={handleSubmit}>Add</Button>
      </Modal.Footer>
    </Modal>
  );
}
