import React, { useState } from 'react';

export default function AddModal({ type, open, onClose, onSave, parentId }) {
  const [input, setInput] = useState('');

  const handleSubmit = () => {
    if (input.trim()) {
      onSave(input, parentId);
      setInput('');
      onClose();
    }
  };

  if (!open) return null;

  const label = {
    category: 'Category Name',
    topic: 'Topic Name',
    list: 'List Title'
  }[type];

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.4)', display: 'flex', justifyContent: 'center', alignItems: 'center'
    }}>
      <div style={{ background: 'white', padding: '20px', borderRadius: 10, width: 300 }}>
        <h3>Add {type}</h3>
        <input
          type="text"
          placeholder={label}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          style={{ width: '100%', marginBottom: 10, padding: 5 }}
        />
        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 10 }}>
          <button onClick={onClose}>Cancel</button>
          <button onClick={handleSubmit}>Save</button>
        </div>
      </div>
    </div>
  );
}
// This component renders a modal for adding categories, topics, or lists.
