import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import Footer from './components/Footer';
import AddModal from './components/AddModal';
import {
  getForumData,
  createCategory,
  createTopic,
  createList
} from './services/api';

function App() {
  const [forumData, setForumData] = useState([]);
  const [modalType, setModalType] = useState(null); // category | topic | list
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedTopic, setSelectedTopic] = useState(null);

  const fetchData = async () => {
    const data = await getForumData();
    setForumData(data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleAdd = (type) => {
    setModalType(type);
  };

  const handleSave = async (value, parentId) => {
    if (modalType === 'category') {
      await createCategory(value);
    } else if (modalType === 'topic') {
      if (!selectedCategory) return alert("Select a category from sidebar first!");
      await createTopic(value, selectedCategory);
    } else if (modalType === 'list') {
      if (!selectedTopic) return alert("Select a topic from sidebar first!");
      await createList(value, selectedTopic);
    }
    await fetchData();
  };

  const handleSidebarSelect = (categoryId, topicId) => {
    setSelectedCategory(categoryId);
    setSelectedTopic(topicId);
  };

  return (
    <>
      <Header onAdd={handleAdd} />
      <div style={{ display: 'flex', minHeight: '80vh' }}>
        <Sidebar
          data={forumData}
          onSelect={handleSidebarSelect}
        />
        <main style={{ flex: 1, padding: '20px' }}>
          <h1>Welcome to the Forum</h1>
          <p>Select a category or topic from the sidebar to add data accordingly.</p>
        </main>
      </div>
      <Footer />

      <AddModal
        type={modalType}
        open={!!modalType}
        onClose={() => setModalType(null)}
        onSave={handleSave}
        parentId={modalType === 'topic' ? selectedCategory : selectedTopic}
      />
    </>
  );
}

export default App;
