import React, { useRef, useEffect } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';

export default function CategoryModal({
  show,
  onHide,
  mode = 'category',
  categoryName = '',
  onCategoryChange = () => {},
  categories = [],
  parentCategoryId = '',
  onParentCategoryChange = () => {},
  subcategoryName = '',
  onSubcategoryChange = () => {},
  onSubmit = () => {}
}) {
  const inputRef = useRef(null);

  useEffect(() => {
    if (!show) return;
    const timer = setTimeout(() => {
      if (inputRef.current) inputRef.current.focus();
    }, 50);
    return () => clearTimeout(timer);
  }, [show, mode]);

  function handleSubmit() {
    if (mode === 'subcategory') {
      if (!parentCategoryId || !subcategoryName.trim()) return;
      onSubmit({ 
        type: 'subcategory', 
        name: subcategoryName.trim(), 
        parentId: parentCategoryId 
      });
    } else {
      if (!categoryName.trim()) return;
      onSubmit({ type: 'category', name: categoryName.trim() });
    }
  }

  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>
          {mode === 'subcategory' ? 'Add Subcategory' : 'Add Category'}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {mode === 'subcategory' ? (
          <>
            <Form.Group className="mb-3">
              <Form.Label>Parent Category</Form.Label>
              <Form.Select
                value={parentCategoryId}
                onChange={(e) => onParentCategoryChange(e.target.value)}
              >
                <option value="">Select parent category</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.name}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Subcategory Name</Form.Label>
              <Form.Control
                type="text"
                value={subcategoryName}
                ref={inputRef}
                onChange={(e) => onSubcategoryChange(e.target.value)}
                placeholder="Enter subcategory name"
              />
            </Form.Group>
          </>
        ) : (
          <Form.Group>
            <Form.Label>Category Name</Form.Label>
            <Form.Control
              type="text"
              value={categoryName}
              ref={inputRef}
              onChange={(e) => onCategoryChange(e.target.value)}
              placeholder="Enter category name"
            />
          </Form.Group>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>Cancel</Button>
        <Button 
          variant="primary" 
          onClick={handleSubmit}
          disabled={mode === 'subcategory' ? 
            (!parentCategoryId || !subcategoryName.trim()) : 
            !categoryName.trim()}
        >
          Add
        </Button>
      </Modal.Footer>
    </Modal>
  );
}
