// src/App.js
import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import AddModal from "./components/AddModal";
import { Toast, ToastContainer, Container, Row, Col } from "react-bootstrap";

function App() {
  const [categories, setCategories] = useState([]);
  const [selectedCat, setSelectedCat] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [toastMessage, setToastMessage] = useState("");
  const [showToast, setShowToast] = useState(false);

  const fetchCategories = async () => {
    try {
      const res = await fetch("http://localhost:5000/forum");
      const data = await res.json();
      setCategories(data);
      // Reset selected category if it no longer exists
      if (selectedCat && !data.find((c) => c._id === selectedCat._id)) {
        setSelectedCat(null);
      }
    } catch (error) {
      console.error("Failed to fetch categories:", error);
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const handleAdd = (type) => {
    setModalType(type);
    setShowModal(true);
  };

  const handleSubmit = async (data) => {
    const url =
      modalType === "category"
        ? "http://localhost:5000/category"
        : "http://localhost:5000/topic";

    const response = await fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    });

    const result = await response.json();
    setShowModal(false);

    const categoryName =
      modalType === "topic"
        ? categories.find((cat) => cat._id === data.categoryId)?.name
        : "";

    setToastMessage(
      modalType === "topic"
        ? `Topic "${data.name}" added to "${categoryName}"`
        : `Category "${data.name}" added successfully`
    );
    setShowToast(true);

    fetchCategories();
  };

  return (
    <>
      <Header onAdd={handleAdd} />
      <Container fluid>
        <Row>
          <Col md={3}>
            <Sidebar categories={categories} onSelectCategory={setSelectedCat} />
          </Col>
          <Col md={9} className="p-3">
            <h4>Welcome to the Forum</h4>
            {selectedCat ? (
              <div>
                <h5>Topics in: {selectedCat.name}</h5>
                <ul>
                  {(selectedCat.topics ?? []).map((topic) => (
                    <li key={topic._id}>{topic.name}</li>
                  ))}
                </ul>
              </div>
            ) : (
              <p>Please select a category to see topics.</p>
            )}
          </Col>
        </Row>
      </Container>

      {showModal && (
        <AddModal
          type={modalType}
          onAdd={handleSubmit}
          onClose={() => {
            setShowModal(false);
            setModalType(null);
          }}
          categories={categories}
        />
      )}

      <ToastContainer position="top-end" className="p-3">
        <Toast
          onClose={() => setShowToast(false)}
          show={showToast}
          delay={3000}
          autohide
          bg="success"
        >
          <Toast.Header closeButton={false}>
            <strong className="me-auto">Forum App</strong>
          </Toast.Header>
          <Toast.Body className="text-white">{toastMessage}</Toast.Body>
        </Toast>
      </ToastContainer>
    </>
  );
}

export default App;
