import React, { useState } from 'react';
import Header from './components/Header';
import AddModal from './components/AddModal';
import { Toast, ToastContainer } from 'react-bootstrap';


function App() {
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(null); // 'category' | 'topic' | 'list'
    const [toastMessage, setToastMessage] = useState('');
  const [showToast, setShowToast] = useState(false);
``
  const handleAdd = (type) => {
    setModalType(type);
    setShowModal(true);
  };

  const handleSubmit = async (data) => {
    let url = '';

    if (modalType === 'category') {
      url = 'http://localhost:5000/category';
    } else if (modalType === 'topic') {
      url = 'http://localhost:5000/topic';
    } else if (modalType === 'list') {
      url = 'http://localhost:5000/list';
    }

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      const result = await response.json();
      console.log(`${modalType} added:`, result);

      setShowModal(false);
    } catch (error) {
      console.error(`Error adding ${modalType}:`, error);
    }
  };

  return (
    <>
      <Header onAdd={handleAdd} />

      {showModal && (
        <AddModal
          type={modalType}
          onAdd={handleSubmit}
          onClose={() => setShowModal(false)}
        />
      )}
    </>
  );
}

export default App;
