// src/components/AddModal.js
import React, { useState, useEffect, useRef } from "react";
import { Modal, Button, Form, Tabs, Tab } from "react-bootstrap";
import Editor from "react-simple-code-editor";
import Prism from "prismjs";

// Import the CSS for Prism themes (choose one)
import "prismjs/themes/prism-tomorrow.css";

// Import Prism languages you want to support
import "prismjs/components/prism-javascript";
import "prismjs/components/prism-python";

export default function AddModal({ type, onAdd, onClose, categories = [] }) {
  const [formData, setFormData] = useState({
    name: "",
    categoryId: "",
    codebase: "",
    output: "",
    status: "active",
  });
  const [language, setLanguage] = useState("javascript");
  const [errors, setErrors] = useState({});
  const inputRef = useRef(null);

  useEffect(() => {
    if (inputRef.current) inputRef.current.focus();
  }, []);

  const validate = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = "Name is required";
    if (type === "topic" && !formData.categoryId)
      newErrors.categoryId = "Category is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Syntax highlighter for react-simple-code-editor
  const highlightCode = (code) =>
    Prism.highlight(code, Prism.languages[language], language);

  const handleSubmit = () => {
    if (!validate()) return;
    const payload =
      type === "topic"
        ? {
            name: formData.name,
            categoryId: formData.categoryId,
            codebase: formData.codebase,
            output: formData.output,
            status: formData.status,
            language,
          }
        : { name: formData.name };

    onAdd(payload);
  };

  return (
    <Modal show onHide={onClose} size="lg" scrollable>
      <Modal.Header closeButton>
        <Modal.Title>Add {type}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="formName" className="mb-3">
            <Form.Label>{type} Name</Form.Label>
            <Form.Control
              ref={inputRef}
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder={`Enter ${type} name`}
              isInvalid={!!errors.name}
              autoFocus
            />
            <Form.Control.Feedback type="invalid">
              {errors.name}
            </Form.Control.Feedback>
          </Form.Group>

          {type === "topic" && (
            <>
              <Form.Group controlId="formCategoryId" className="mb-3">
                <Form.Label>Category</Form.Label>
                <Form.Select
                  name="categoryId"
                  value={formData.categoryId}
                  onChange={handleChange}
                  isInvalid={!!errors.categoryId}
                >
                  <option value="">Select Category</option>
                  {categories.map((cat) => (
                    <option key={cat._id} value={cat._id}>
                      {cat.name}
                    </option>
                  ))}
                </Form.Select>
                <Form.Control.Feedback type="invalid">
                  {errors.categoryId}
                </Form.Control.Feedback>
              </Form.Group>

              <Form.Group controlId="formLanguage" className="mb-3">
                <Form.Label>Language</Form.Label>
                <Form.Select
                  name="language"
                  value={language}
                  onChange={(e) => setLanguage(e.target.value)}
                >
                  <option value="python">Python</option>
                  <option value="javascript">JavaScript DSA</option>
                  <option value="react">React JS</option>
                  <option value="node">Node JS</option>
                  <option value="ml">Machine Learning</option>
                  <option value="ai">Artificial Intelligence</option>
                </Form.Select>
              </Form.Group>

              <Form.Group controlId="formCodebase" className="mb-3">
                <Form.Label>Codebase</Form.Label>
                <div
                  style={{
                    border: "1px solid #ced4da",
                    borderRadius: "0.25rem",
                    minHeight: "150px",
                    fontFamily: "monospace",
                    fontSize: 14,
                    padding: "10px",
                    backgroundColor: "#2d2d2d",
                    color: "#ccc",
                    overflow: "auto",
                  }}
                >
                  <Editor
                    value={formData.codebase}
                    onValueChange={(codebase) =>
                      setFormData((prev) => ({ ...prev, codebase }))
                    }
                    highlight={highlightCode}
                    padding={10}
                    style={{
                      fontFamily: '"Fira code", monospace',
                      fontSize: 14,
                      outline: 0,
                      minHeight: 140,
                    }}
                  />
                </div>
              </Form.Group>

              <Form.Group controlId="formOutput" className="mb-3">
                <Form.Label>Output</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={2}
                  name="output"
                  value={formData.output}
                  onChange={handleChange}
                  placeholder="Enter output of code"
                />
              </Form.Group>

              <Form.Group controlId="formStatus" className="mb-3">
                <Form.Label>Status</Form.Label>
                <Form.Select
                  name="status"
                  value={formData.status}
                  onChange={handleChange}
                >
                  <option value="active">Active</option>
                  <option value="inactive">Inactive</option>
                </Form.Select>
              </Form.Group>
            </>
          )}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Add
        </Button>
      </Modal.Footer>
    </Modal>
  );
}
