import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container, Row, Col, Button, Modal, Form, Navbar, Nav
} from 'react-bootstrap';

function App() {
  const [categories, setCategories] = useState([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState('');
  const [topicName, setTopicName] = useState('');
  const [showModal, setShowModal] = useState(false);

  const fetchCategories = async () => {
    try {
      const res = await axios.get('http://localhost:5000/forum');
      setCategories(res.data.categories || []);
    } catch (err) {
      console.error('Failed to fetch categories:', err);
    }
  };

  const handleAddTopic = async () => {
    try {
      if (!selectedCategoryId || !topicName) {
        alert('Please select category and enter topic name');
        return;
      }

      await axios.post('http://localhost:5000/forum/topic', {
        categoryId: selectedCategoryId,
        name: topicName,
      });

      setTopicName('');
      setSelectedCategoryId('');
      setShowModal(false);
      fetchCategories();
    } catch (err) {
      console.error('Error creating topic:', err);
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  return (
    <Container fluid>
      {/* Header */}
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="#home">Forum App</Navbar.Brand>
        <Nav className="ms-auto">
          <Button variant="success" className="me-2" onClick={() => setShowModal(true)}>
            Add Topic
          </Button>
          <Button variant="outline-light" className="me-2">Login</Button>
          <Button variant="outline-light">Register</Button>
        </Nav>
      </Navbar>

      <Row>
        {/* Sidebar */}
        <Col md={2} className="bg-light p-3">
          <h5>Categories</h5>
          <ul className="list-group">
            {categories.map(cat => (
              <li key={cat._id} className="list-group-item">
                {cat.name}
                <ul>
                  {cat.topics?.map(topic => (
                    <li key={topic._id}>{topic.name}</li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </Col>

        {/* Main Content */}
        <Col md={10} className="p-3">
          <h4>Welcome to the Forum!</h4>
          <p>Select a category and add a topic using the button above.</p>
        </Col>
      </Row>

      {/* Footer */}
      <footer className="bg-dark text-light text-center p-2 mt-4">
        &copy; {new Date().getFullYear()} Forum App
      </footer>

      {/* Add Topic Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Topic</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formCategory">
              <Form.Label>Select Category</Form.Label>
              <Form.Select
                value={selectedCategoryId}
                onChange={(e) => setSelectedCategoryId(e.target.value)}
              >
                <option value="">-- Select --</option>
                {categories.map(cat => (
                  <option key={cat._id} value={cat._id}>{cat.name}</option>
                ))}
              </Form.Select>
            </Form.Group>

            <Form.Group className="mt-3" controlId="formTopic">
              <Form.Label>Topic Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter topic name"
                value={topicName}
                onChange={(e) => setTopicName(e.target.value)}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleAddTopic}>
            Add Topic
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default App;
