import React from 'react';

export default function Sidebar({ data, onSelect }) {
  return (
    <aside style={{ width: '20%', background: '#f4f4f4', padding: '10px' }}>
      {data.map(cat => (
        <div key={cat._id} style={{ marginBottom: 10 }}>
          <div
            style={{ fontWeight: 'bold', cursor: 'pointer' }}
            onClick={() => onSelect(cat._id, null)}
          >
            {cat.name}
          </div>
          {cat.topics?.map(topic => (
            <div key={topic._id} style={{ marginLeft: 10 }}>
              <div
                style={{ cursor: 'pointer' }}
                onClick={() => onSelect(cat._id, topic._id)}
              >
                üìÅ {topic.name}
              </div>
              {topic.lists?.map(list => (
                <div key={list._id} style={{ marginLeft: 10 }}>
                  üìù {list.title}
                </div>
              ))}
            </div>
          ))}
        </div>
      ))}
    </aside>
  );
}
